
public class RegistroProduto {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// Variáveis declaradas 
		int codigo;
		String descricao;
		double precoCusto;
		double precoVenda;
		int quantidadeEstoque;
		
		// Atribuição de valor 
		
		codigo = 51436;
		descricao = "Bola de futebol";
		precoCusto = 50;
		precoVenda = 90;
		quantidadeEstoque = 500;
		
		// Printar 
		
		System.out.println("Codigo - " + codigo); 
		System.out.println("Descrição: " + descricao); 
		System.out.println("Preço Custo R$" + precoCusto); 
		System.out.println("Preço Venda R$" + precoVenda); 
		System.out.println("Quantidade em estoque: " + quantidadeEstoque+" UNIDADES");
		
	
			
			
	}

}
